
public class prog4 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 int[] array = {9, 29, 34, 35,30,1,10,20,30};
			 
		 for(int i=0;i<=array.length;i++)
		 {
			 if(array[i]%2==0)
				 
			 {
				 System.out.println("Even"+array[i]);
			 }
			 else
			 {
				 System.out.println("Odd"+array[i]);
			 }
		 }
		
		}

}
